mapper.update:
UPDATE account
account_no = #{accountNo}, account_holder = #{accountHolder}, bank_code_no = #{bankCodeNo}, reference = #{reference}, account_category_code_no = #{accountCategoryCodeNo}
WHERE account_reg_no = #{accountRegNo}

mapper.select:
SELECT account_reg_no, account_no, reference, bank_code_no, bank_code_name, account_holder, account_category_code_no, account_category_code_name, usage_status_code_no FROM account
AND usage_status_code_no = #{searchKeyword}
AND usage_status_code_no = #{searchKeyword}
AND account_reg_no = #{searchKeyword}
AND LOWER(reference) LIKE '%'||#{searchKeyword}||'%'
