mapper.update:
 	UPDATE branch
 	
	 	BRANCH_MANAGER_NAME = #{branchManagerName}, BRANCH_MANAGER_PHONE = #{branchManagerPhone}, BRANCH_TEL = #{branchTel}, 
	 	LOCAL_CODE_NO = #{localCodeNo}, ZIP_CODE = #{zipCode}, ADDRESS = #{address}, DETAIL_ADDRESS = #{detailAdress},
	 	BUSINESS_LICENSE_NO = #{businessLicenseNo}, USAGE_STATUS_CODE = #{branchStatusCodeNo}, BRANCH_NAME = #{branchName}
 	
 	WHERE BRANCH_NO = #{branchNo}

mapper.select:
 	SELECT COUNT(*)
 	FROM ( SELECT BRANCH_NO, BRANCH_NAME, LOCAL_CODE_NO
 			FROM branch	 			
 			
 				
 					 
						BRANCH_NO = #{searchKeyword}
					
					 
						BRANCH_NAME LIKE '%'||#{searchKeyword}||'%'
					
					 
						LOCAL_CODE_NO = #{searchKeyword}
					 
 				
 			 ) countTable
