mapper.insert:
INSERT
  INTO	APPOINTMENT
  			(
  			APPOINTMENT_NO
  		,	APPOINTMENT_DATE
  		,	EMPLOYEE_NO
  		,	PRE_DEPART_CODE_NO
  		,	APPOINTMENT_DEPART_CODE_NO
  		,	PRE_RANK_CODE_NO
  		,	APPOINTMENT_RANK_CODE_NO
  		,	APPOINTMENT_STATUS_CODE_NO
  		,	REFER
  			)
VALUES	
		(
			SEQ_APPOINTMENT_NO.NEXTVAL
		,	#{appointDate}
		,	#{employeeNo}
		,	#{preDepartCodeNo}
		,	#{appointDepartCodeNo}
		,	#{preRankCodeNo}
		,	#{appointRankCodeNo}
		,	#{appointmentStatusCodeNo}
		,	#{reference}
		)

mapper.update:
	UPDATE	APPOINTMENT
	   SET	
	   		(
	   		APPOINTMENT_DATE			=	#{appointDate}
	   	 ,	APPOINTMENT_DEPART_CODE_NO	=	#{appointDepartCodeNo}
	   	 ,	APPOINTMENT_RANK_CODE_NO	=	#{appointRankCodeNo}
	   	 ,	REFER						=	#{reference}
	   		)

  			EMPLOYEE_NO = #{employeeNo}
